import React from "react";
import { useState } from "react";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogTitle from "@mui/material/DialogTitle";
import dayjs from "dayjs";

import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { DatePicker, TimePicker } from "@mui/x-date-pickers";

export default function AddTraining(props) {
  const [open, setOpen] = useState(false);
  const [timeValue, setTimeValue] = useState("");
  const [training, setTraining] = useState({
    date: null,
    activity: "",
    duration: "",
    customer: "",
  });

  const handleClickOpen = () => {
    setTraining({
      date: props.date,
      activity: props.activity,
      duration: props.duration,
      customer: props.customer,
    });
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleDateChange = (date) => {
    const newDate = dayjs(training.date).set("date", date.date()).set("month", date.month()).set("year", date.year());
    setTraining({ ...training, date: newDate.toISOString() });
  };

  const handleTimeInputChange = (e) => {
    setTimeValue(e.target.value);
  };

  const handleTimeBlur = (e) => {
    if (e.target.value === "") {
      setTimeValue("00:00");
      const newDate = dayjs(training.date).set("hour", 0).set("minute", 0);
      setTraining({ ...training, date: newDate.toISOString() });
    }
  };




  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTraining({ ...training, [name]: value });
  };

  const updateTraining = () => {
    const newTraining = {
      date: dayjs(training.date).format('YYYY-MM-DD[T]HH:mm:ss.SSS[Z]'),
      activity: training.activity,
      duration: parseInt(training.duration, 10),
      customer: training.customer,
    };

    console.log('newTraining:', newTraining); 

    props.saveTraining(newTraining);
    handleClose();
  };


  return (
    <div>
      <Button onClick={handleClickOpen}>Add training</Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Add training</DialogTitle>
        <DialogContent>
          <LocalizationProvider dateAdapter={AdapterDayjs}>
            <DatePicker
              value={dayjs(training.date)}
              onChange={handleDateChange}
              renderInput={(params) => <TextField {...params} />}
              format="DD.MM.YY"
            />
            <TimePicker
              value={timeValue === "" ? null : dayjs(training.date)}
              onChange={handleTimeChange}
              onInputChange={handleTimeInputChange}
              renderInput={(params) => (
                <TextField {...params} value={timeValue} onBlur={handleTimeBlur} />
              )}
              format="HH:mm"
            />


          </LocalizationProvider>
          <TextField
            margin="dense"
            name="activity"
            value={training.activity}
            onChange={handleInputChange}
            label="activity"
            fullWidth
          />
          <TextField
            margin="dense"
            name="duration"
            value={training.duration}
            onChange={handleInputChange}
            label="duration"
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={updateTraining}>Save</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
