import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';


function Calendar() {
  const [trainings, setTrainings] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);


  useEffect(() => {
    fetchTrainings();
  }, []);



  const fetchTrainings = async () => {
    try {
      const trainingsResponse = await fetch('https://traineeapp.azurewebsites.net/gettrainings');
      const trainingsData = await trainingsResponse.json();

      const formattedTrainings = trainingsData.map((training) => {
        const customer = training.customer;
        const title = customer
          ? `${training.activity} / ${customer.firstname} ${customer.lastname}`
          : `${training.activity} / Unknown Customer`;

        const startTime = new Date(training.date);
        const endTime = new Date(startTime.getTime() + training.duration * 60000);

        return {
          title,
          start: startTime,
          end: endTime,
        };
      });

      setTrainings(formattedTrainings);
    } catch (err) {
      console.error(err);
    }
  };

  const handleEventClick = (info) => {
    setSelectedEvent(info.event);
  };

  const handleDateClick = (info) => {
    console.log('Clicked on date:', info.dateStr);
  };

  const renderSelectedEvent = () => {
    if (!selectedEvent) return null;

    const { title, start, end } = selectedEvent;

    return (
      <div>
        <h3>Event Details</h3>
        <p>Title: {title}</p>
        <p>Start: {start.toLocaleString()}</p>
        <p>End: {end.toLocaleString()}</p>
      </div>
    );
  };


  return (
    <div className="calendar">
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        headerToolbar={{
          left: 'prev,next today',
          center: 'title',
          right: 'dayGridMonth,timeGridWeek,timeGridDay',
        }}
        events={trainings}
        dateClick={handleDateClick}
        eventClick={handleEventClick}
      />
       {renderSelectedEvent()}
    </div>
  );
}

export default Calendar;
