import React, { useState, useEffect } from "react";
import { AgGridReact } from "ag-grid-react";
import dayjs from "dayjs";

import "ag-grid-community/styles/ag-grid.css";
import "ag-grid-community/styles/ag-theme-material.css";

function Traininglist() {
  const [trainings, setTrainings] = useState([]);

  const [columnDefs] = useState([
    {
      headerName: "Date",
      field: "date",
      sortable: true,
      filter: true,
      valueFormatter: (params) => dayjs(params.value).format("DD.MM.YYYY HH:mm"),
    },
    { headerName: "Duration", field: "duration", sortable: true, filter: true },
    { headerName: "Activity", field: "activity", sortable: true, filter: true },
    { headerName: "Customer", field: "customer", sortable: true, filter: true },
  ]);

  const getCustomerById = async (id) => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/customers/${id}`);
      if (!response.ok) {
        throw new Error(`Error fetching customer by ID: ${response.statusText}`);
      }
      const data = await response.json();
      return data;
    } catch (error) {
      console.error("Error fetching customer:", error);
    }
  };

  const getCustomerTrainings = async (id) => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/customers/${id}/trainings`);
      if (!response.ok) {
        throw new Error(`Error fetching customer's trainings: ${response.statusText}`);
      }
      const { content: data } = await response.json();
      return data;
    } catch (error) {
      console.error("Error fetching customer's trainings:", error);
    }
  };

  const getTrainings = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/trainings`);
      if (!response.ok) {
        throw new Error(`Error fetching trainings data: ${response.statusText}`);
      }
      const { content: trainings } = await response.json();

      const trainingsWithCustomer = [];

      for (const training of trainings) {
        const customerData = await getCustomerById(training.customer.id);

        trainingsWithCustomer.push({
          date: training.date,
          duration: training.duration,
          activity: training.activity,
          customer: `${customerData.firstname} ${customerData.lastname}`,
        });
      }

      setTrainings(trainingsWithCustomer);
    } catch (error) {
      console.error("Error fetching trainings:", error);
    }
  };

  useEffect(() => {
    getTrainings();
  }, []);

  return (
    <>
      <div className="ag-theme-material" style={{ width: "80%", height: 600, margin: "auto" }}>
        <AgGridReact rowData={trainings} columnDefs={columnDefs} pagination={true} paginationPageSize={10} />
      </div>
    </>
  );
}

export default Traininglist;
