import React from "react";
import { useState } from "react";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogTitle from "@mui/material/DialogTitle";
import dayjs from "dayjs";

import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { DateTimePicker } from "@mui/x-date-pickers/DateTimePicker";

export default function AddTraining(props) {
  const [open, setOpen] = useState(false);
  const [training, setTraining] = useState({
    date: dayjs().toISOString(),
    activity: "",
    duration: "",
    customer: "",
  });

  const handleClickOpen = () => {
    setTraining({
      date: props.date,
      activity: props.activity,
      duration: props.duration,
      customer: props.customer,
    });
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === "date") {
      // format the date string to ISO-8601 before updating state
      const date = dayjs(value, "DD.MM.YY HH:mm").toISOString();
      setTraining({ ...training, [name]: date });
    } else {
      setTraining({ ...training, [name]: value });
    }
  };

  const updateTraining = () => {
    const newTraining = {
      date: training.date,
      activity: training.activity,
      duration: training.duration,
      customer: training.customer,
    };
    props.saveTraining(newTraining);
    handleClose();
  };

  return (
    <div>
      <Button onClick={handleClickOpen}>Add training</Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Add training</DialogTitle>
        <DialogContent>
          <LocalizationProvider dateAdapter={AdapterDayjs}>
            <DateTimePicker
              value={training.date}
              onChange={(date) => {
                setTraining({ ...training, date: date.toISOString() });
              }}
              renderInput={(params) => <TextField {...params} />}
              format="DD.MM.YY HH:mm"
            />
          </LocalizationProvider>
          <TextField
            margin="dense"
            name="activity"
            value={training.activity}
            onChange={handleInputChange}
            label="activity"
            fullWidth
          />
          <TextField
            margin="dense"
            name="duration"
            value={training.duration}
            onChange={handleInputChange}
            label="duration"
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={updateTraining}>Save</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
