import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';

const API_URL = process.env.REACT_APP_API_URL;

function Calendar() {
  const [trainings, setTrainings] = useState([]);

  useEffect(() => {
    fetchTrainings();
  }, []);

  const fetchCustomerById = async (customerId) => {
    try {
      const response = await fetch(API_URL + '/customers/' + customerId);
      const data = await response.json();
      return data;
    } catch (err) {
      console.error(err);
      return null;
    }
  };


  const fetchTrainings = async () => {
    try {
      const trainingsResponse = await fetch(API_URL + '/trainings');
      const trainingsData = await trainingsResponse.json();

      const fetchAndFormatTraining = async (training) => {
        const customer = await fetchCustomerById(training.customerId); // Replace "training.customerId" with the correct property name
        const title = customer
          ? `${training.activity} / ${customer.firstname} ${customer.lastname}`
          : `${training.activity} / Unknown Customer`;

        const startTime = new Date(training.date);
        const endTime = new Date(startTime.getTime() + training.duration * 60000);

        return {
          title,
          start: startTime,
          end: endTime,
        };
      };

      const formattedTrainings = await Promise.all(
        trainingsData.content.map((training) => fetchAndFormatTraining(training))
      );

      setEvents(formattedTrainings);
    } catch (err) {
      console.error(err);
    }
  };


  const handleDateClick = (info) => {
    console.log('Clicked on date:', info.dateStr);
  };

  return (
    <div className="calendar">
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        headerToolbar={{
          left: 'prev,next today',
          center: 'title',
          right: 'dayGridMonth,timeGridWeek,timeGridDay',
        }}
        events={trainings}
        dateClick={handleDateClick}
      />
    </div>
  );
}

export default Calendar;
